using System;
using System.Collections.Generic;
using System.Configuration;
using System.Linq;
using System.Windows.Forms;

namespace HackSystem.Host
{
    /// <summary>
    /// 配置控制器
    /// </summary>
    public static class ConfigController
    {
        private static readonly Configuration UnityConfig = ConfigurationManager.OpenExeConfiguration(Application.ExecutablePath);
        
        /// <summary>
        /// 装填默认配置
        /// </summary>
        public static void LoadDefaultConfig()
        {
            LogController.Info("装载默认配置信息...");

            //TODO : 默认配置写在这里
            Dictionary<string, string> DefaultConfigDictionary = new Dictionary<string, string>
            {
                { "StartUpFile", "AnimationStartUp.dll" },
                { "StartUpName", "SerpentineStartUpClass" },
                { "LogonFile", "HTMLLogon.dll" },
                { "LogonName", "DefaultHTMLLogonClass" },
                { "UserName", "Leon" },
                { "Password", "123456" },
                { "HeadPortrait", "" }
            };

            foreach (string Key in DefaultConfigDictionary.Keys)
            {
                if (!UnityConfig.AppSettings.Settings.AllKeys.Contains(Key))
                    UnityConfig.AppSettings.Settings.Add(Key, DefaultConfigDictionary[Key]);
            }

            UnityConfig.Save();
        }

        /// <summary>
        /// 读取配置
        /// </summary>
        /// <param name="Key">配置Key</param>
        /// <returns>配置信息</returns>
        public static string GetConfig(string Key)
        {
            LogController.Debug("获取配置项Key: {0}", Key);
            string ConfigValue = string.Empty;
            try
            {
                KeyValueConfigurationElement ConfigurationElement = UnityConfig.AppSettings.Settings[Key];
                ConfigValue = ConfigurationElement?.Value ?? string.Empty;
                LogController.Debug(">>> ConfigValue[\"{0}\"] = {1}", Key, ConfigValue.Length<=1024?ConfigValue: "0x" + ConfigValue.GetHashCode().ToString("X"));
                return ConfigValue;
            }
            catch (Exception ex)
            {
                LogController.Error("读取配置失败 : Key = {0}, Message:{1}",Key, ex.Message);
                return string.Empty;
            }
        }

        /// <summary>
        /// 设置配置
        /// </summary>
        /// <param name="Key">配置Key</param>
        /// <param name="Value">配置Value</param>
        public static void SetConfig(string Key, string Value)
        {
            LogController.Debug("设置配置项Key:{0}, Value:{1}", Key, Value);
            try
            {
                UnityConfig.AppSettings.Settings.Remove(Key);
                UnityConfig.AppSettings.Settings.Add(Key, Value);
                UnityConfig.Save();
            }
            catch (Exception ex)
            {
                LogController.Error("设置配置失败.Key:{0}, Value:{1}, Message:{2}", Key, Value, ex.Message);
                throw ex;
            }
        }

    }
}
